#!/usr/bin/env node
var program = require('commander'),
    fs = require('fs-extra'),
    chalk = require('chalk'),
    inquirer = require('inquirer'),
    utils = require('./utils'),
    pjson = require('../package.json');

program
    .version(pjson.version)
    .usage('[command]')
    .command('init', 'initialize new pattern library')
    .command('update', 'update existing pattern library')
    .command('new', 'create a new component')
    .command('edit', 'edit a component and/or group')
    .command('delete', 'delete component and/or group')
    .command('list', 'list components')
    .option('-I, --instance', 'output the instance version number')
    .parse(process.argv);

if (!process.argv.slice(2).length) {
    program.outputHelp();
}

/**
 * Get instance version number.
 */
if (program.instance) {
    try {
        if(utils.getConfig()) {
            utils.init();
            console.log(chalk.grey("This Astrum instance version is: ") + chalk.green(utils.$data.version));
        }
    } catch (e) {
        console.log(chalk.red("Astrum has not been initialised."));
    }
}

var fs = require('fs-extra'),
    chalk = require('chalk'),
    inquirer = require('inquirer'),
    dir = require('global-modules'),
    mkdirp = require('mkdirp'),
    isWindows = require('is-windows'),
    pjson = require('../package.json');

module.exports = {

    module_path: dir + '/' + pjson.name,
    $root: process.cwd(),
    $config: null,
    $data: null,
    $pjson: pjson,

    init: function() {
        var _this = this;

        // Get config.
        try {
            _this.$config = _this.getConfig();
        } catch (e) {
            throw(new Error(chalk.red("No astrum-config.json file found in project root.")));
        }

        // Get data.
        try {
            _this.$data = _this.getData();
        } catch (e) {
            throw(new Error(chalk.red("No data.json file found in " + _this.$config.path )));
        }
    },

    setup: function(path, callback) {
        var _this = this,
            error = false;

        fs.exists(path, function(r) {
            if (r) {
                throw(new Error(chalk.red('Pattern library has already been initialized.')));
            }
        });

        _this.saveConfig(path, function() {
            mkdirp(path, function (err) {
                if (err) {
                    console.error(chalk.red('Error: ' + err));
                    error = true;
                }

                fs.copy(_this.pathify(_this.module_path + '/_template'), path, function (err) {
                    if (err) {
                        console.log(chalk.red('Error: ' + err));
                        error = true;
                    }

                    _this.init();
                    _this.updateVersion(pjson.version);

                    return callback();
                });
            });

            return ! error;
        });
    },

    updateVersion: function(number) {
        this.$data.version = number;

        this.saveData(function(){});
    },

    update: function(callback) {
        var _this = this;

        fs.exists(_this.$config.path, function(r) {
            if (!r) {
                throw(new Error(chalk.red('No pattern library found to update.')));
            }
        });

        fs.copy(_this.pathify(_this.module_path + '/_template/app'), _this.$config.path + '/app');
        fs.copy(_this.pathify(_this.module_path + '/_template/index.html'), _this.$config.path + '/index.html');
        fs.copy(_this.pathify(_this.module_path + '/_template/LICENSE.txt'), _this.$config.path + '/LICENSE.txt');

        _this.updateVersion(pjson.version);

        /**
         * Version 1.7.0 introduced customisable titles.
         * If updating from pre-1.7.0 we need to add in the
         * default titles.
         */
        if(!_this.$data.theme.hasOwnProperty("titles")) {
            _this.$data.theme.titles = {
                "library_title": "Pattern Library",
                "pages_title": "Overview",
                "components_title": "Components"
            }

            _this.saveData(function() {});
        }

        return callback();
    },

    getConfig: function() {
        var _this = this;

        return JSON.parse(fs.readFileSync(_this.pathify(_this.$root + '/astrum-config.json')));
    },

    getData: function() {
        var _this = this;

        return JSON.parse(fs.readFileSync(_this.pathify(_this.$config.path + '/data.json')));
    },

    outputList: function(components, groups) {

        console.log();
        console.log(chalk.grey('Legend:'));
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log('[i]: index');
        console.log('%s: group', chalk.green('green'));
        console.log('%s: component', chalk.yellow('yellow'));
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log();

        if(groups.length) {
            for (var i = 0; i < groups.length; i++) {
                var g = groups[i];

                console.log('[%s] %s:', i, chalk.green(g.name));

                var k = 0;
                for (var j = 0; j < groups[i].components.length; j++) {
                    var c = groups[i].components[j];

                    if (c.group === g.name) {
                        console.log('     %s [%s] %s', String.fromCharCode(0x21B3), k, chalk.yellow(c.name));
                        k++;
                    }
                }
            }
        } else {
            console.log('Your pattern library is empty.');
        }

        console.log();
    },

    saveConfig: function(path, callback) {
        var _this = this,
            error = false;

        fs.writeFile(_this.pathify(this.$root + '/astrum-config.json'), JSON.stringify({
            path: path
        }, null, 4), function (err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
            }

            return callback();
        });

        return ! error;
    },

    saveData: function(callback) {
        var _this = this,
            error = false;

        fs.writeFile(_this.pathify(_this.$config.path + '/data.json'), JSON.stringify(_this.$data, null, 4), function (err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
            }

            return callback();
        });

        return ! error;
    },

    getGroupIndex: function(group) {
        return this.$data.groups.findIndex(function(item) {
            return item.name == group;
        });
    },

    getComponentIndex: function(group_name) {
        var _this = this,
            parts = group_name.split("/"),
            groupIndex = _this.getGroupIndex(parts[0]);

        if (groupIndex !== -1) {
            for (var i = 0; i < _this.$data.groups[groupIndex].components.length; i++) {
                var c = _this.$data.groups[groupIndex].components[i];

                if (c.name == parts[1]) {
                    return i;
                }
            }
        }
    },

    createGroupFolder: function(group_path, callback) {
        var _this = this;

        callback = typeof callback !== 'undefined' ? callback : function(){};

        fs.mkdir(group_path, function(err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
                return;
            }

            _this.createGroupDescription(group_path);

            return callback();
        });
    },

    createGroupDescription: function(group_path) {
        var _this = this;

        fs.exists(_this.pathify(group_path + '/description.md'), function(r) {

            if(!r) {
                fs.writeFile(_this.pathify(group_path + '/description.md'), '', function(err) {
                    if (err) {
                        console.log(chalk.red('Error: ' + err));
                        error = true;
                        return;
                    }
                });
            }
        });
    },

    deleteGroupFolder: function(group) {
        var _this = this,
            group_path = this.$config.path + '/components/' + group,
            error = false;

        fs.remove(_this.pathify(group_path), function(err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
            }
        });

        return ! error;
    },

    createComponentFolder: function(component_path, callback) {
        callback = typeof callback !== 'undefined' ? callback : function(){};

        var _this = this,
            error = false;

        fs.mkdir(_this.pathify(component_path), function(err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
                return;
            }

            fs.writeFile(_this.pathify(component_path + '/markup.html'), '', function(err) {
                if (err) {
                    console.log(chalk.red('Error: ' + err));
                    error = true;
                    return;
                }

                fs.writeFile(_this.pathify(component_path + '/description.md'), '', function(err) {
                    if (err) {
                        console.log(chalk.red('Error: ' + err));
                        error = true;
                        return;
                    }

                    return callback();
                });
            });
        });

        return ! error;
    },

    createComponentFiles: function(component) {
        var _this = this,
            group_path = this.$config.path + '/components/' + component.group,
            component_path = group_path + '/' + component.name,
            error = false;

        fs.exists(_this.pathify(group_path), function(r) {
            if(r) {
                _this.createComponentFolder(component_path);
            } else {
                _this.createGroupFolder(group_path, function() {
                    _this.createComponentFolder(component_path);
                });
            }
        });

        return ! error;
    },

    moveComponentFiles: function(original_component, edited_component) {
        var _this = this,
            o_path = this.$config.path + '/components/' + original_component.group + '/' + original_component.name,
            d_group_path = this.$config.path + '/components/' + edited_component.group,
            d_path = d_group_path + '/' + edited_component.name,
            error = false;

        if(o_path != d_path) {
            fs.move(_this.pathify(o_path), _this.pathify(d_path), function(err) {
                if (err) {
                    console.error(chalk.red('Error: ' + err));
                    error = true;
                }

                _this.createGroupDescription(d_group_path);
            });
        }

        return ! error;
    },

    moveGroupFolder: function(original_group, edited_group) {
        var _this = this,
            oPath = this.$config.path + '/components/' + original_group.name,
            dPath = this.$config.path + '/components/' + edited_group.name,
            error = false;

        if(oPath != dPath) {
            fs.move(_this.pathify(oPath), _this.pathify(dPath), function(err) {
                if (err) {
                    console.error(chalk.red('Error: ' + err));
                    error = true;
                }
            });
        }

        return ! error;
    },

    deleteComponentFiles: function(group_name) {
        var _this = this,
            component_path = this.$config.path + '/components/' + group_name,
            error = false;

        fs.remove(_this.pathify(component_path), function(err) {
            if (err) {
                console.log(chalk.red('Error: ' + err));
                error = true;
            }
        });

        return ! error;
    },

    validateComponent: function(group_name) {
        var parts = group_name.split("/");

        // Check format
        if (parts.length !== 2) {
            console.log(chalk.red('Error: A new component name must comprise of a group, a single forward-slash, and a name e.g. buttons/default.'));
            return false;
        }

        // Check for duplication
        if (this.componentExists(group_name)) {
            console.log(chalk.red('Error: A component with the name "%s" already exists in the "%s" group.'), parts[1], parts[0]);
            return false;
        }

        return true;
    },

    componentExists: function(group_name) {
        var _this = this,
            parts = group_name.split("/"),
            groupIndex = _this.getGroupIndex(parts[0]);

        if (groupIndex !== -1) {
            for (var i = 0; i < _this.$data.groups[groupIndex].components.length; i++) {
                var c = _this.$data.groups[groupIndex].components[i];

                if (c.name == parts[1]) {
                    return true;
                }
            }
        }

        return false;
    },

    groupExists: function(group_name) {
        var parts = group_name.split("/");

        return this.getGroupIndex(parts[0]) == -1 ? false : true;
    },

    getGroupPositionChoices: function() {
        var choices = [{
                name: 'Position first',
                value: 0
            }],
            n = 1;

        for (var i = 0; i < this.$data.groups.length; i++) {
            var g = this.$data.groups[i];

            choices.push({
                name: 'Position after ' + g.name,
                value: n
            });

            n++;
        }

        return choices;
    },

    getGroupChoices: function(exclude_group) {
        exclude_group = typeof exclude_group !== 'undefined' ? exclude_group : null;

        var choices = [];

        for (var i = 0; i < this.$data.groups.length; i++) {
            var g = this.$data.groups[i];

            if(exclude_group != g.name) {
                choices.push({
                    name: g.name,
                    value: g.name
                });
            }
        }

        choices.push(new inquirer.Separator());

        choices.push({
            name: 'Create a new group...',
            value: 'create_new_group'
        });

        return choices;
    },

    getComponentChangePositionChoices: function(component) {
        var _this = this,
            choices = [],
            message,
            currentPosition = null,
            groupIndex = _this.getGroupIndex(component.group),
            first = true,
            passedCurrentPosition = false;

        for (var i = 0; i < _this.$data.groups[groupIndex].components.length; i++) {
            var c = _this.$data.groups[groupIndex].components[i];

            if(first && c.name != component.name) {
                choices.push({
                    name: 'Position first',
                    value: 0
                });
            }
            first = false;

            if(c.name == component.name) {
                if(i != 0) choices.pop();
                currentPosition = i;
                passedCurrentPosition = true;
            }

            if(c.name != component.name) {
                choices.push({
                    name: 'Position after ' + chalk.yellow(c.name),
                    value: passedCurrentPosition ? i : i + 1
                });
            }
        }

        choices.push(new inquirer.Separator());

        message = currentPosition == 0 ? 'Keep current first position' : 'Keep current position after ' + chalk.yellow(_this.$data.groups[groupIndex].components[currentPosition - 1].name);

        choices.push({
            name: message,
            value: currentPosition
        });

        return choices;
    },

    getGroupChangePositionChoices: function(group) {
        var _this = this,
            choices = [],
            message,
            currentPosition = null,
            groupIndex = _this.getGroupIndex(group.name),
            first = true,
            passedCurrentPosition = false;

        for (var i = 0; i < _this.$data.groups.length; i++) {
            var g = _this.$data.groups[i];

            if(first && g.name != group.name) {
                choices.push({
                    name: 'Position first',
                    value: 0
                });
            }
            first = false;

            if(g.name == group.name) {
                if(i != 0) choices.pop();
                currentPosition = i;
                passedCurrentPosition = true;
            }

            if(g.name != group.name) {
                choices.push({
                    name: 'Position after ' + chalk.yellow(g.name),
                    value: passedCurrentPosition ? i : i + 1
                });
            }
        }

        choices.push(new inquirer.Separator());

        message = currentPosition == 0 ? 'Keep current first position' : 'Keep current position after ' + chalk.yellow(_this.$data.groups[groupIndex - 1].name);

        choices.push({
            name: message,
            value: currentPosition
        });

        return choices;
    },

    getComponentPositionChoices: function(group) {
        var _this = this,
            groupIndex = _this.getGroupIndex(group),
            choices = [{
                name: 'Position first',
                value: 0
            }];

        for (var i = 0; i < _this.$data.groups[groupIndex].components.length; i++) {
            var c = _this.$data.groups[groupIndex].components[i];
            choices.push({
                name: 'Position after ' + chalk.yellow(c.name),
                value: i + 1
            });
        }

        return choices;
    },

    getGroupComponentCount: function(group) {
        return this.$data.groups[this.getGroupIndex(group)].components.length;
    },

    getGroupCount: function(group) {
        return this.$data.groups.length;
    },

    getComponent: function(group_name) {
        var _this = this,
            parts = group_name.split('/');

        for(var i = 0; i < _this.$data.groups.length; i++) {
            for(var j = 0; j < _this.$data.groups[i].components.length; j++) {
                var c = _this.$data.groups[i].components[j];

                if(c.group == parts[0] && c.name == parts[1]) {
                    return c;
                }
            }
        }

        return false;
    },

    validateSlug: function(string) {
        var slug = string.match(/^[a-z0-9-]+$/);

        return this.validateString(string) && slug && !! slug.length;
    },

    validateString: function(string) {
        return string != '';
    },

    pathify: function(path) {
        if(isWindows()) {
            return path.replace(/(\/)/g, "\\");
        }

        return path;
    }
};

#!/usr/bin/env node
var program = require('commander'),
    fs = require('fs-extra'),
    chalk = require('chalk'),
    inquirer = require('inquirer'),
    utils = require('./utils');

utils.init();

program
    .description(chalk.yellow('Delete a pattern library component or group.'))
    .option('-g, --group [group_name]', 'delete group');

/**
 * Override argv[1] so that usage command is
 * formatted correctly.
 */
process.argv[1] = 'astrum delete';

program.parse(process.argv);

/**
 * Automatically output help if no parameters are passed.
 */
if (!process.argv.slice(2).length) {
    program.outputHelp();
}

/**
 * Delete individual component.
 */
var group_name = program.args[0];
if (group_name) {
    var parts = group_name.split('/');

    if (utils.componentExists(group_name) !== false) {
        var componentIndex = utils.getComponentIndex(group_name),
            groupIndex = utils.getGroupIndex(parts[0]);

        inquirer.prompt([
            {
                type: 'confirm',
                name: 'delete',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Delete "' + parts[1] + '" component:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return chalk.red('Are you sure you want to delete this component?');
                },
                default: false
            }
        ]).then(function (answers) {
            if (!answers.delete) {
                console.log();
                console.log(chalk.grey('----------------------------------------------------------------'));
                console.log(chalk.green('\u2713 Deletion cancelled.'));
                console.log(chalk.grey('----------------------------------------------------------------'));
                console.log();
                return;
            }

            if (answers.delete) {
                var componentCount = utils.getGroupComponentCount(parts[0]);

                utils.$data.groups[groupIndex].components.splice(componentIndex, 1);
                utils.deleteComponentFiles(group_name);

                if (componentCount == 1) {
                    inquirer.prompt([
                        {
                            type: 'confirm',
                            name: 'delete_group',
                            message: chalk.red('Deleting this component will leave it\'s parent group empty. Delete the "' + parts[0] + '" group as well?'),
                            default: true
                        }
                    ]).then(function (answers) {

                        if (!answers.delete_group) {

                            utils.saveData(function () {
                                console.log();
                                console.log(chalk.grey('----------------------------------------------------------------'));
                                console.log(chalk.green('\u2713 Component deleted successfully.'));
                                console.log(chalk.grey('----------------------------------------------------------------'));
                                console.log();
                            });
                        }

                        if (answers.delete_group) {
                            utils.$data.groups.splice(groupIndex, 1);
                            utils.deleteGroupFolder(parts[0]);

                            utils.saveData(function () {
                                console.log();
                                console.log(chalk.grey('----------------------------------------------------------------'));
                                console.log(chalk.green('\u2713 Group and component deleted successfully.'));
                                console.log(chalk.grey('----------------------------------------------------------------'));
                                console.log();
                            });
                        }
                    });
                } else {

                    utils.saveData(function () {
                        console.log();
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log(chalk.green('\u2713 Component deleted successfully.'));
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log();
                    });
                }
            }
        });
    } else {
        console.log(chalk.red('Error: Component not found.'));
    }
}

/**
 * Delete entire group.
 */
if (program.group) {
    var existingGroupIndex = utils.getGroupIndex(program.group);

    if (existingGroupIndex !== -1) {
        var group = utils.$data.groups[existingGroupIndex];

        inquirer.prompt([
            {
                type: 'confirm',
                name: 'delete',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Delete the "' + group.name + '" group and all it\'s components:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return chalk.red('Are you sure you want to delete this group?');
                },
                default: false
            }
        ]).then(function (answers) {
            if (!answers.delete) {
                console.log();
                console.log(chalk.grey('----------------------------------------------------------------'));
                console.log(chalk.green('\u2713 Deletion cancelled.'));
                console.log(chalk.grey('----------------------------------------------------------------'));
                console.log();
                return;
            }

            if (answers.delete) {
                utils.$data.groups.splice(existingGroupIndex, 1);
                utils.deleteGroupFolder(group.name);

                utils.saveData(function () {
                    console.log();
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    console.log(chalk.green('\u2713 Group and component deleted successfully.'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    console.log();
                });
            }
        });
    } else {
        console.log(chalk.red('Error: Group not found.'));
    }
}

#!/usr/bin/env node
var program = require('commander'),
    fs = require('fs-extra'),
    chalk = require('chalk'),
    inquirer = require('inquirer'),
    utils = require('./utils');

utils.init();

program
    .usage('[group_name/component_name]')
    .description(chalk.yellow('Edit an existing pattern library component or group.'))
    .option('-g, --group [group_name]', 'edit group details');

/**
 * Override argv[1] so that usage command is
 * formatted correctly.
 */
process.argv[1] = 'astrum edit';

program.parse(process.argv);

/**
 * Automatically output help if no parameters are passed.
 */
if (!process.argv.slice(2).length) {
    program.outputHelp();
}

var group_name = program.args[0];
if (group_name) {
    var parts = group_name.split('/'),
        existingComponentIndex = utils.getComponentIndex(group_name),
        existingGroupIndex = utils.getGroupIndex(parts[0]);

    if (existingComponentIndex !== undefined) {
        var component = utils.getComponent(group_name);

        inquirer.prompt([
            {
                name: 'name',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Edit component details:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return 'Component name:'
                },
                validate: function (str) {
                    return utils.validateSlug(str);
                },
                default: component.name
            },
            {
                name: 'title',
                message: 'Component title:',
                validate: function (str) {
                    return utils.validateString(str);
                },
                default: component.title
            },
            {
                when: function () {
                    return !component.type || component.type !== 'colors';
                },
                type: 'list',
                name: 'width',
                message: function () {
                    return 'Component width:'
                },
                choices: [
                    {
                        name: 'Full width',
                        value: 'full'
                    },
                    {
                        name: 'Half width',
                        value: 'half'
                    }
                ],
                default: component.width
            },
            {
                when: function () {
                    return !program.type || program.type !== 'colors';
                },
                type: 'confirm',
                name: 'sample_dark_background',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Component options:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return 'Apply a dark background to the code sample?'
                },
                default: function() {
                    if(component.hasOwnProperty('options') &&
                       component.options.hasOwnProperty('sample_dark_background')) {
                        return component.options.sample_dark_background;
                    }

                    return false;
                }
            },
            {
                when: function () {
                    return !program.type || program.type !== 'colors';
                },
                type: 'confirm',
                name: 'disable_code_sample',
                message: function () {
                    return 'Disable code sample?'
                },
                default: function() {
                    if(component.hasOwnProperty('options') &&
                       component.options.hasOwnProperty('disable_code_sample')) {
                        return component.options.disable_code_sample;
                    }

                    return false;
                }
            },
            {
                type: 'confirm',
                name: 'change_group',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Manage component group association:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return 'Change component group:';
                },
                default: false
            },
            {
                when: function (response) {
                    return response.change_group;
                },
                type: 'list',
                name: 'new_group',
                message: 'Select a new group:',
                choices: utils.getGroupChoices(parts[0])
            },
            {
                when: function (response) {
                    return !response.change_group || response.new_group != 'create_new_group';
                },
                type: 'list',
                name: 'component_position',
                message: function (response) {
                    if (!response.change_group) {
                        return 'Change the position for the component in the "' + component.group + '" group:'
                    } else {
                        return 'Select a position for the component in the "' + response.new_group + '" group:'
                    }
                },
                choices: function (response) {
                    if (!response.change_group) {
                        return utils.getComponentChangePositionChoices(component);
                    } else {
                        return utils.getComponentPositionChoices(response.new_group);
                    }
                },
                default: function (response) {
                    if (!response.change_group) {
                        return utils.getGroupComponentCount(component.group) - 1;
                    }

                    return 0;
                }
            },
            {
                when: function (response) {
                    return response.change_group && response.new_group == 'create_new_group';
                },
                name: 'group',
                message: function () {
                    console.log();
                    console.log(chalk.grey('New group details:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return 'Group name:';
                },
                validate: function (str) {
                    return utils.validateSlug(str);
                },
                choices: utils.getGroupChoices(parts[0])
            },
            {
                when: function (response) {
                    return response.change_group && response.new_group == 'create_new_group';
                },
                name: 'group_title',
                message: 'Group title:',
                choices: utils.getGroupChoices()
            },
            {
                when: function (response) {
                    return response.change_group && response.new_group == 'create_new_group';
                },
                type: 'list',
                name: 'group_position',
                message: 'Select group position:',
                choices: utils.getGroupPositionChoices()
            },
            {
                when: function (response) {
                    return response.change_group && utils.getGroupComponentCount(component.group) == 1;
                },
                type: 'confirm',
                name: 'delete_group',
                message: chalk.red('Moving this component will leave it\'s current parent group empty. Delete the "' + component.group + '" group in the process?'),
                default: true
            }
        ]).then(function (answers) {
            var originalComponent = utils.$data.groups[existingGroupIndex].components[existingComponentIndex],
                editedComponent = {},
                editedGroupIndex,
                error = false;

            // Store edited component details
            editedComponent.name = answers.name;
            editedComponent.title = answers.title;
            if (answers.width != 'full') editedComponent.width = answers.width;

            // Retain original details
            if(originalComponent.type) editedComponent.type = originalComponent.type;
            if(originalComponent.colors) editedComponent.colors = originalComponent.colors;
            if(originalComponent.options) editedComponent.options = originalComponent.options;

            // Apply new options
            if(!answers.sample_dark_background &&
               editedComponent.hasOwnProperty('options') &&
                editedComponent.options.hasOwnProperty('sample_dark_background')) {
                delete editedComponent.options.sample_dark_background;
            }

            if (answers.sample_dark_background) {
                if(!editedComponent.hasOwnProperty('options')) { editedComponent.options = {}; }
                editedComponent.options.sample_dark_background = true;
            }

            if(!answers.disable_code_sample &&
               editedComponent.hasOwnProperty('options') &&
                editedComponent.options.hasOwnProperty('disable_code_sample')) {
                delete editedComponent.options.disable_code_sample;
            }

            if (answers.disable_code_sample) {
                if(!editedComponent.hasOwnProperty('options')) { editedComponent.options = {}; }
                editedComponent.options.disable_code_sample = true;
            }

            //// If creating a new group
            if (answers.new_group == 'create_new_group') {
                var newGroup = {};

                // Store new group details
                newGroup.name = answers.group;
                newGroup.title = answers.group_title;
                newGroup.components = [];

                // Add new group to data
                utils.$data.groups.splice(answers.group_position, 0, newGroup);

                // Set edited components group to new group name
                editedComponent.group = answers.group;
                editedGroupIndex = answers.group_position;

                // Refresh existing groups index
                existingGroupIndex = utils.getGroupIndex(originalComponent.group);
            } else {
                editedComponent.group = answers.new_group ? answers.new_group : originalComponent.group;
                editedGroupIndex = utils.getGroupIndex(editedComponent.group);
            }

            // Check for duplicate data
            if (originalComponent.name != editedComponent.name && utils.componentExists(editedComponent.group + '/' + editedComponent.name)) {
                console.log(chalk.red('Error: A component with same name already exists in the group.'));
                error = true;
            }

            if (!error) {
                // Remove original component in data
                utils.$data.groups[existingGroupIndex].components.splice(existingComponentIndex, 1);
                utils.$data.groups[editedGroupIndex].components.splice(answers.component_position, 0, editedComponent);

                // Move component files
                if (utils.moveComponentFiles(originalComponent, editedComponent)) {

                    if (answers.delete_group) {
                        utils.$data.groups.splice(existingGroupIndex, 1);
                        utils.deleteGroupFolder(originalComponent.group);

                        console.log();
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log(chalk.green('\u2713 Original group deleted successfully.'));
                    }

                    utils.saveData(function () {
                        if (!answers.delete_group) {
                            console.log();
                            console.log(chalk.grey('----------------------------------------------------------------'));
                        }
                        console.log(chalk.green('\u2713 Pattern library data saved successfully.'));
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log();
                        console.log(chalk.yellow('Edit your component markup in ' + utils.$config.path + '/components/' + editedComponent.group + '/' + editedComponent.name + '/markup.html'));
                        console.log(chalk.yellow('Edit your component description in ' + utils.$config.path + '/components/' + editedComponent.group + '/' + editedComponent.name + '/description.md (Markdown supported)'));
                        console.log();
                    });
                }
            }
        });

        // Return error
    } else {
        console.log(chalk.red('Error: Component not found.'))
    }
}

if (program.group) {
    var existingGroupIndex = utils.getGroupIndex(program.group);

    if (existingGroupIndex !== false) {
        var group = utils.$data.groups[existingGroupIndex];

        inquirer.prompt([
            {
                name: 'name',
                message: function () {
                    console.log();
                    console.log(chalk.grey('Edit group details:'));
                    console.log(chalk.grey('----------------------------------------------------------------'));
                    return 'Group name:'
                },
                validate: function (str) {
                    return utils.validateSlug(str);
                },
                default: group.name
            },
            {
                name: 'title',
                message: function () {
                    return 'Group title:'
                },
                validate: function (str) {
                    return utils.validateString(str);
                },
                default: group.title
            },
            {
                type: 'list',
                name: 'group_position',
                message: 'Change the position for the group:',
                choices: utils.getGroupChangePositionChoices(group),
                default: utils.getGroupCount(group) - 1
            }
        ]).then(function (answers) {
            var originalGroup = utils.$data.groups[existingGroupIndex],
                editedGroup = {},
                error = false;

            editedGroup.name = answers.name;
            editedGroup.title = answers.title;
            editedGroup.components = [];

            for (var i = 0; i < originalGroup.components.length; i++) {
                editedGroup.components[i] = originalGroup.components[i];
                editedGroup.components[i].group = answers.name;
            }

            // Check for duplicate data
            if (originalGroup.name != editedGroup.name && utils.groupExists(editedGroup.name)) {
                console.log(chalk.red('Error: Group with same name already exists in.'));
                error = true;
            }

            if (!error) {
                // Remove original group in data
                utils.$data.groups.splice(existingGroupIndex, 1);

                // Add edited group to data
                utils.$data.groups.splice(answers.group_position, 0, editedGroup);

                // Move component files
                if (utils.moveGroupFolder(originalGroup, editedGroup)) {

                    utils.saveData(function () {
                        console.log();
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log(chalk.green('\u2713 Pattern library data saved successfully.'));
                        console.log(chalk.grey('----------------------------------------------------------------'));
                        console.log();

                        if (originalGroup.name != editedGroup.name) {
                            console.log(chalk.yellow('Group components have been moved to ' + utils.$config.path + '/components/' + editedGroup.name));
                            console.log();
                        }
                    });
                }
            }
        });
    } else {
        console.log(chalk.red('Error: Group not found.'));
    }
}

