                                console.log(chalk.yellow('    "#FFFFFF"'));
                                console.log(chalk.yellow(']'));
                                console.log();
                                console.log(chalk.yellow('You can also add complimentary colors by comma separating the values e.g.:'));
                                console.log();
                                console.log(chalk.yellow('"colors": ['));
                                console.log(chalk.yellow('    "#7da9f9,#507ed3",'));
                                console.log(chalk.yellow('    "#f469a7,#c14c80",'));
                                console.log(chalk.yellow('    "#60ceb8,#3fa18d",'));
                                console.log(chalk.yellow('    "#f5d13f,#f5a63f",'));
                                console.log(chalk.yellow('    "#e199e5,#c776cb"'));
                                console.log(chalk.yellow(']'));
                                console.log();
                            } else {
                                console.log();
                                console.log(chalk.yellow('Add your group description to ' + utils.$config.path + '/components/' + newComponent.group + '/description.md (Markdown supported)'));
                                console.log(chalk.yellow('Add your component markup to ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/markup.html'));
                                console.log(chalk.yellow('Add your component description to ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/description.md (Markdown supported)'));
                                console.log();
                            }
                        });
                    }
                });

                // Else prompt to position new component in group
            } else {

                inquirer.prompt([
                    {
                        type: 'list',
                        name: 'component_position',
                        message: 'Select component position in the "' + newComponent.group + '" group:',
                        choices: utils.getComponentPositionChoices(newComponent.group),
                        default: utils.getComponentPositionChoices(newComponent.group).length - 1
                    }
                ]).then(function (answers) {
                    var groupIndex = utils.getGroupIndex(newComponent.group);

                    utils.$data.groups[groupIndex].components.splice(answers.component_position, 0, newComponent);

                    if (utils.createComponentFiles(newComponent)) {
                        utils.saveData(function () {
                            console.log();
                            console.log(chalk.grey('----------------------------------------------------------------'));
                            console.log(chalk.green('\u2713 Pattern library data saved successfully.'));
                            console.log(chalk.grey('----------------------------------------------------------------'));

                            if (typeColor) {
                                console.log();
                                console.log(chalk.yellow('Ignore ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/markup.html'));
                                console.log(chalk.yellow('Add your component description to ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/description.md (Markdown supported)'));
                                console.log();
                                console.log(chalk.yellow('Locate this component in your data.json file and add your colors to the "colors"'));
                                console.log(chalk.yellow('array that has been created for you. Colors must be hex values e.g.:'));
                                console.log();
                                console.log(chalk.yellow('"colors": ['));
                                console.log(chalk.yellow('    "#4c4c4c",'));
                                console.log(chalk.yellow('    "#7d8284",'));
                                console.log(chalk.yellow('    "#a6b1b5",'));
                                console.log(chalk.yellow('    "#e6eaf2",'));
                                console.log(chalk.yellow('    "#FFFFFF"'));
                                console.log(chalk.yellow(']'));
                                console.log();
                                console.log(chalk.yellow('You can also add complimentary colors by comma separating the values e.g.:'));
                                console.log();
                                console.log(chalk.yellow('"colors": ['));
                                console.log(chalk.yellow('    "#7da9f9,#507ed3",'));
                                console.log(chalk.yellow('    "#f469a7,#c14c80",'));
                                console.log(chalk.yellow('    "#60ceb8,#3fa18d",'));
                                console.log(chalk.yellow('    "#f5d13f,#f5a63f",'));
                                console.log(chalk.yellow('    "#e199e5,#c776cb"'));
                                console.log(chalk.yellow(']'));
                                console.log();
                            } else {
                                console.log();
                                console.log(chalk.yellow('Add your component markup to ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/markup.html'));
                                console.log(chalk.yellow('Add your component description to ' + utils.$config.path + '/components/' + newComponent.group + '/' + newComponent.name + '/description.md (Markdown supported)'));
                                console.log();
                            }
                        });
                    }
                });
            }
        });

        // Return error
    } else if (group_name == true) {
        console.log(chalk.red('Error: To create a new component supply a "group/name" parameter.'))
    }
}

#!/usr/bin/env node
var Command = require('commander').Command,
    program = require('commander'),
    fs = require('fs-extra'),
    chalk = require('chalk'),
    inquirer = require('inquirer'),
    utils = require('./utils');

utils.init();

program
    .usage('[path]')
    .description(chalk.yellow('Update an existing pattern library.'))
    .option('-f, --force', 'Force update the Astrum instance. ');

/**
 * Override argv[1] so that usage command is
 * formatted correctly.
 */
process.argv[1] = 'astrum update';

program.parse(process.argv);

/**
 * Update pattern library.
 */
oldVersion = utils.$data.version;
newVersion = utils.$pjson.version;

/**
 * Force restore instance.
 */
if (program.force && newVersion == oldVersion) {
    utils.update(function () {
        console.log();
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log(chalk.green('\u2713 Pattern library forcibly restored to ' + newVersion + '.'));
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log();
    });

/**
 * Update instance.
 */
} else if(newVersion !== oldVersion) {
    utils.update(function () {
        console.log();
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log(chalk.green('\u2713 Pattern library successfully updated from ' + oldVersion + ' to ' + newVersion + '.'));
        console.log(chalk.grey('----------------------------------------------------------------'));
        console.log();
    });

/**
 * Instance up-to-date.
 */
} else {
    console.log();
    console.log(chalk.grey('----------------------------------------------------------------'));
    console.log(chalk.yellow('Pattern library is up-to-date.'));
    console.log(chalk.grey('----------------------------------------------------------------'));
    console.log();
}

/**
 * Component component
 */
var ndplComponent = Vue.extend({

    data: function() {
        return {
            loaded: false,
            hide_sample_code: false
        }
    },

    props: {
        component: {
            required: true
        }
    },

    computed: {
        inline_styles: function() {
            var _this = this,
                styles = '';

            // These inline style are only applied after the component has fully loaded
            if(_this.loaded) {
                if(_this.component.options.sample_min_height) {
                    styles += 'min-height:' + _this.component.options.sample_min_height + 'px;';
                }
            }

            if(_this.component.options.sample_overflow_hidden) {
                styles += 'overflow: hidden;';
            }
            if(_this.component.options.sample_background_color) {
                styles += 'background-color:' + _this.component.options.sample_background_color + ' !important;';
            }

            return styles;
        }
    },

    watch: {
        'component.html': function() {
            var _this = this;

            // Apply syntax highlighting when component html is loaded
            if(this.component.html.length) {
                _this.$root.applySyntaxHighlighting(_this.$el);
            }
        }
    },

    ready: function() {
        var _this = this;

        // Listen for loaded event
        _this.$on('loaded', function() {

            // Monitor scroll and resize events and update navigation active state appropirately
            window.addEventListener('scroll', _this.updateActive);
            window.addEventListener('resize', _this.updateActive);

            _this.setHideSample(function() {
                _this.loaded = true;
            });
        });

        // Listen for resizing event
        _this.$on('resizing', function(is_resizing) {
            _this.loaded = false;

            if(! is_resizing) {
                _this.setHideSample(function() {
                    _this.loaded = true;
                });
            }
        });

        if (_this.component.html) {
            _this.$root.applySyntaxHighlighting(_this.$el);
        }
    },

    methods: {

        /**
         * Update component active in navigation.
         */
        updateActive: function() {
            var _this = this;

            // If scroll position is great than or equal to component offset top - 60 pixels
            // and scroll position is less than component offset top plus component height plus 60 pixels
            // and active component is not this component
            if(_this.$root && _this.$root.scroll_position >= _this.$el.offsetTop - 60 &&
                _this.$root.scroll_position < _this.$el.offsetTop + _this.$el.offsetHeight) {

                // If not currently auto scrolling to component
                // and component is not active
                if(!_this.$root.scrolling_to &&
                    !_this.isActive(_this.component)) {

                    // Set this component to active
                    _this.$root.active_components.push(_this.component);
                    _this.$root.open_group = null;
                    _this.$root.updateHash(_this.component.id);
                }
            } else {

                // If not currently auto scrolling to component
                if(_this.$root && !_this.$root.scrolling_to) {

                    // Loop through active components and remove this component
                    for (var i = 0; i < _this.$root.active_components.length; i++) {
                        var component = _this.$root.active_components[i];

                        if (component.id === _this.component.id) {
                            _this.$root.active_components.splice(i, 1);

                            return;
                        }
                    }
                }
            }
        },

        /**
         * Is component active in navigation.
         *
         * @param component
         * @returns {boolean}
         */
        isActive: function(component) {
            var _this = this;

            for (var i = 0; i < _this.$root.active_components.length; i++) {
                var c = _this.$root.active_components[i];

                if (component.id === c.id) {
                    return true;
                }
            }

            return false;
        },

        /**
         * Should invert text based on hex brightness.
         *
         * @param hex
         * @returns {boolean}
         */
        shouldInvertText: function(hex) {
            var rgb = this.$root.convertHexToRgb(hex),
                brightness = this.$root.getColorBrightness(rgb);

            return brightness > 210;
        },

        /**
         * Should apply border based on hex brightness.
         *
         * @param hex
         * @returns {boolean}
         */
        shouldApplyBorder: function(hex) {
            var rgb = this.$root.convertHexToRgb(hex),
                brightness = this.$root.getColorBrightness(rgb);

            return brightness > 240;
        },

        /**
         * Set component sample element to be hidden if the sample
         * itself is hidden in the assets CSS stylesheet.
         *
         * @param callback
         */
        setHideSample: function(callback) {
            callback = typeof callback !== 'undefined' ?  callback : function() {};

            var _this = this;

            _this.hide_sample_code = false;

            setTimeout(function() {
                if(_this.$el.querySelector('.ndpl-component__code')) {

                    /**
                     * Auto-detect hidden sample.
                     */
                    if (_this.$el.querySelector('.ndpl-component__sample').offsetHeight <= 74 &&
                        !_this.component.options.disabled_auto_sample_hiding) {
                        _this.hide_sample_code = true;
                    }

                    /**
                     * If manually specifying when to show nad hide samples.
                     */
                    if (_this.component.options.disabled_auto_sample_hiding &&
                        _this.component.options.disabled_auto_sample_hiding.hasOwnProperty('show_on_mobile') &&
                        _this.component.options.disabled_auto_sample_hiding.hasOwnProperty('show_on_desktop')) {


                        if(_this.$root.mobile_view) {
                            _this.hide_sample_code = !_this.component.options.disabled_auto_sample_hiding.show_on_mobile;
                        } else {
                            _this.hide_sample_code = !_this.component.options.disabled_auto_sample_hiding.show_on_desktop;
                        }
                    }
                }

                callback();
            }, 0);
        },

        /**
         * Is sample code visible.
         *
         * @returns {boolean}
         */
        isCodeVisible: function() {
            var _this = this;

            return ! _this.hide_sample_code;
        }
    }
});
Vue.component('ndpl-component', ndplComponent);

/**
 * Group component
